import lp
def solve(s):
    return lp.longestPalindrome(s);
    pass
def sp(*a):
    assert solve(*a[:-1]) == a[-1]
    pass
def test():
    sp("", "")
    sp("a", "a")
    sp("123", "1")
    sp("122", "22")
    sp("121", "121")
    sp("1212121", "1212121")
    sp("xabcbad", "abcba")
    pass

